  ListNode* rotateRight(ListNode* head, int k) {
      if(!head||!head->next) return head;
      int len = 1;
      ListNode *p = head;
      while(p->next){
          p = p->next;
          len++;
      }
      k = k%len;
      k = len-k;
      if(k==len) return head;
      ListNode *q = head,*pre;
      while(k--){
          pre = q;
          q = q->next;
      }
      pre->next = nullptr;
      p->next = head;
      return q;
  }

